mapper.select:
SELECT n.*,
       a.username as creator_name
FROM news n
LEFT JOIN account a ON n.create_by = a.id
WHERE 1=1

    AND n.category = #{category}


    AND n.status = #{status}

ORDER BY n.is_top DESC, n.publish_time DESC, n.create_time DESC
LIMIT #{offset}, #{limit}

SELECT COUNT(*)
FROM news
WHERE 1=1

    AND category = #{category}


    AND status = #{status}

mapper.update:
UPDATE news
SET status = #{status}
WHERE id = #{id}

UPDATE news
SET is_top = #{isTop}
WHERE id = #{id}

UPDATE news
SET view_count = view_count + 1
WHERE id = #{id}

UPDATE news
SET like_count = like_count + 1
WHERE id = #{id}
