mapper.select:
SELECT p.*, a.username, a.avatar
FROM post p
LEFT JOIN account a ON p.user_id = a.id
WHERE 1=1

    AND p.category = #{category}


    AND p.status = #{status}

ORDER BY p.create_time DESC
LIMIT #{offset}, #{limit}

SELECT COUNT(*) FROM post
WHERE 1=1

    AND category = #{category}


    AND status = #{status}

SELECT p.*, a.username, a.avatar
FROM post p
LEFT JOIN account a ON p.user_id = a.id
WHERE p.user_id = #{userId}
ORDER BY p.create_time DESC
LIMIT #{offset}, #{limit}
