mapper.select:
SELECT si.*,
       a.username,
       a.id as avatar
FROM service_info si
LEFT JOIN account a ON si.user_id = a.id
WHERE 1=1

    AND si.service_type = #{serviceType}


    AND si.publish_type = #{publishType}


    AND si.status = #{status}

ORDER BY si.create_time DESC
LIMIT #{offset}, #{limit}

SELECT COUNT(*)
FROM service_info
WHERE 1=1

    AND service_type = #{serviceType}


    AND publish_type = #{publishType}


    AND status = #{status}

SELECT * FROM service_info
WHERE user_id = #{userId}
ORDER BY create_time DESC

mapper.update:
UPDATE service_info
SET view_count = view_count + 1
WHERE id = #{id}

UPDATE service_info
SET application_count = application_count + 1
WHERE id = #{id}

UPDATE service_info
SET status = #{status}
WHERE id = #{id}
